import React, { useState } from 'react';
import Cta from '../components/Cta';
import Footer from '../components/Footer';
import Hero from '../components/Hero';

import { useLocation } from 'react-router-dom';
import Cookies from 'universal-cookie';


const Home = () => {
    // Initialize cookies 
    const cookies = new Cookies();

    const handleLogout = () => {
        cookies.remove('id_token');
    }

    const location = useLocation();
    // Try to get the id_token from the url params
    const params = new URLSearchParams(location.hash);
    const paramsString = location.hash;
    const indexOfIdToken = params.indexOf('id_token=');
    const indexOfNextAmpersand = params.indexOf('&', indexOfIdToken);
    const id_token = params.substring(indexOfIdToken, indexOfNextAmpersand);
    console.log('id_token: ', id_token);

    if (id_token) {
        cookies.set('id_token', id_token, { path: '/' });
    }


    return (
        <>
            <Hero />
            <Cta/>
            <Footer />
        </>

    )
}

export default Home;
