import React, { useState } from 'react';
import NavBar from '../components/Navbar/NavBar';
import Footer from '../components/Footer';
import {useDocTitle} from '../components/CustomHook';
import axios from 'axios';
import Notiflix from 'notiflix';

const Contact = () => {
    // useDocTitle('MLD | Molad e Konsult - Send us a message')
    useDocTitle('SpeakTranscribe | Upload your voice file')
    const [errors, setErrors] = useState([])
    const [voice_file, setFile] = useState(null)

    const clearErrors = () => {
        setErrors([])
    }

    const clearInput = () => {
        setFile(null)
    }

    const sendFile = (e) => {
        e.preventDefault();
        document.getElementById('submitBtn').disabled = true;
        document.getElementById('submitBtn').innerHTML = 'Loading...';
        console.log(voice_file);
        let fData = new FormData();
        fData.append('voice_file', voice_file)
        console.log(fData);

        axios({
            method: "put",
            url: process.env.UPLOAD_VOICE_TO_S3_API + "cs690-transcribe/file1.m4a",
            data: fData,
            headers: {
                'Content-Type':  'audio/mp4',
                'Authorization': 'eyJraWQiOiJVRXdGWklOUHFUbTU2RFpZY1NxZXVMZHAzNEJsNkhTeEgxY3ZBdXVQeFFRPSIsImFsZyI6IlJTMjU2In0..yY5PU3rj-TpDHJBqsEoy5BtfquXXePUR-twyimb5Nhah1oefG4dpZmuKMNDcpbeWngTfB56a3CRpi75NeSrRcOhLZDh8oI06-mEB58IYEwYjGPqvwNXZ5ueM-pRB7mbjx01ev_3xZWTWjQ3YR7Vd7n5vBO1dKdyGNR75PCzNeZAiM6Zw3J3XApetU-ScGw0RmCe5dvNmxo6Y4eU4ZYaTA8QH4RLwnPlek40unkgQeYgyBzZr-ZSzdFjSwnY4OY1KObVALuHjXBWQ5DYCfIK7Nm9soPD-RjfFpZzCcPQX-mY__vQKOGB8w3BSChctjvOA79Rf7K4cz5bhRb8VtwxC0A'
            }
        })
        .then(function (response) {
            document.getElementById('submitBtn').disabled = false;
            document.getElementById('submitBtn').innerHTML = 'send message';
            clearInput()
            //handle success
            Notiflix.Report.success(
                'Success',
                response.data.message,
                'Okay',
            );
        })
        .catch(function (error) {
            document.getElementById('submitBtn').disabled = false;
            document.getElementById('submitBtn').innerHTML = 'send message';
            //handle error
            const { response } = error;
            if(response.status === 500) {
                Notiflix.Report.failure(
                    'An error occurred',
                    response.data.message,
                    'Okay',
                );
            }
            if(response.data.errors !== null) {
                setErrors(response.data.errors)
            }

        });
    }
    return (
        <>
            <div>
                <NavBar />
            </div>
            <div className="flex justify-center items-center mt-8 w-full bg-white py-12 lg:py-24 bg-gray-200">
                <div className="container mx-auto my-8 px-4 lg:px-20" data-aos="zoom-in">

                <form onSubmit={sendFile}>

                    <div className="w-full bg-white p-8 my-4 md:px-12 lg:w-9/12 lg:pl-20 lg:pr-40 mr-auto rounded-2xl shadow-2xl">
                        <div className="flex">
                            <h1 className="font-bold text-center lg:text-left text-blue-900 uppercase text-4xl">Send us a message</h1>
                        </div>
                        <div className="my-4">
                                <input
                                    name="voice_file" 
                                    className="w-full bg-gray-100 text-gray-900 mt-2 p-3 rounded-lg focus:outline-none focus:shadow-outline"
                                    type="file" 
                                    placeholder="Voice File*"
                                    value={voice_file}
                                    onChange={(e)=> setFile(e.target.files[0])}
                                    onKeyUp={clearErrors}
                                />
                                {errors && 
                                    <p className="text-red-500 text-sm">{errors.voice_file}</p>
                                }
                        </div>

                        <div className="my-2 w-1/2 lg:w-2/4">
                            <button type="submit" id="submitBtn" className="uppercase text-sm font-bold tracking-wide bg-gray-500 hover:bg-blue-900 text-gray-100 p-3 rounded-lg w-full 
                                    focus:outline-none focus:shadow-outline">
                                Send File
                            </button>
                        </div>
                </div>
                </form>

                </div>
            </div>
            <Footer />
        </>


    )
}

export default Contact;
